// エラー情報を抽出してChatwork用メッセージを構築
const errorData = $input.all()[0].json;

// 基本情報の取得
const workflowName = errorData.workflow?.name || 'Unknown Workflow';
const executionId = errorData.execution?.id || 'Unknown';
const errorMessage = errorData.execution?.error?.message || 'No error message';
const failedNodeName = errorData.execution?.lastNodeExecuted || 'Unknown Node';

// 実行URLの構築
const baseUrl = 'https://michi-gaeru.app.n8n.cloud';
const executionUrl = `${baseUrl}/workflow/${errorData.workflow?.id}/executions/${executionId}`;

// 日本時間でタイムスタンプ
const now = new Date();
const jstTime = new Date(now.getTime() + (9 * 60 * 60 * 1000));
const timestamp = jstTime.toISOString().replace('T', ' ').substring(0, 19);

// Chatwork用メッセージ（絵文字で視認性向上）
const message = `[info][title](devil) ワークフローエラー発生[/title]
■ ワークフロー: ${workflowName}
■ 失敗ノード: ${failedNodeName}
■ エラー内容: ${errorMessage}
■ 実行ID: ${executionId}
■ 発生時刻: ${timestamp} (JST)
■ 詳細確認: ${executionUrl}[/info]`;

return [{ json: { message } }];
