const error = $input.first().json;

const workflowName = error.workflow?.name || 'Unknown';
const workflowId = error.workflow?.id || '';
const nodeName = error.execution?.error?.node?.name || 'Unknown';
const errorMessage = error.execution?.error?.message || 'No message';
const executionId = error.execution?.id || '';

const timestamp = new Date().toLocaleString('ja-JP', { timeZone: 'Asia/Tokyo' });

const baseUrl = 'https://michi-gaeru.app.n8n.cloud';
const executionUrl = executionId ? `${baseUrl}/workflow/${workflowId}/executions/${executionId}` : '';

let message = `[info][title]n8n エラー通知[/title]`;
message += `発生時刻: ${timestamp}\n`;
message += `ワークフロー: ${workflowName}\n`;
message += `失敗ノード: ${nodeName}\n`;
message += `エラー内容: ${errorMessage}`;
if (executionUrl) {
  message += `\n\n実行ログ: ${executionUrl}`;
}
message += `[/info]`;

return [{ json: { message } }];
